/* ===== BASE ICON COMPONENT ===== */
export function Icon(props: IconVariants) {
  const {
    type,
    name,
    weight,
    size,
    color,
    className = "",
    role,
    ariaLabel,
    onClick,
    ...rest
  } = props;

  const combinedClasses = `${iconStyles.base} ${iconStyles.size[size]} ${
    iconStyles[type][color]
  } ${className}`;

  const commonProps = {
    className: combinedClasses,
    role,
    "aria-label": ariaLabel || name,
    onClick,
    ...rest,
  };

  /* ===== ICON PATH HANDLER ===== */
  const getIconPath = () => {
    switch (name) {
      case "twitter":
        return twitterIcon[weight as "light" | "normal" | "bold"];
      case "telegram":
        return telegramIcon[weight as "light" | "normal" | "bold"];
      case "github":
        return githubIcon[weight as "light" | "normal" | "bold"];
      case "discord":
        return discordIcon[weight as "light" | "normal" | "bold"];
      case "instagram":
        return instagramIcon[weight as "light" | "normal" | "bold"];
      case "globe":
        return globeIcon[weight as "light" | "normal" | "bold"];
      case "handcoins":
        return handcoinsIcon[weight as "light" | "normal" | "bold"];
      default:
        return "";
    }
  };

  /* ===== SHARED ICONS ===== */
  /* ===== SVG ELEMENT ===== */
  const svgElement = (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 32 32"
      {...commonProps}
    >
      <path d={getIconPath()} />
    </svg>
  );

  /* ===== RENDER BASED ON TYPE ===== */
  if (type === "iconLink") {
    const { href, target, rel } = props;
    return (
      <a href={href} target={target} rel={rel}>
        {svgElement}
      </a>
    );
  }

  if (type === "iconButton") {
    return (
      <button type="button" onClick={onClick}>
        {svgElement}
      </button>
    );
  }

  return svgElement;
}

