import { BadgeVariants, iconStyles, IconVariants } from "./styles.ts";

// Global SVG handler
export function Icon(props: IconVariants) {
  const {
    type,
    name,
    weight,
    size,
    color,
    className = "",
    role,
    ariaLabel,
    onClick,
    ...rest
  } = props;

  // Combine classes based on type, size, and color
  const combinedClasses = `${iconStyles.base} ${iconStyles.size[size]} ${
    iconStyles.color[color]
  } ${iconStyles.type[type]} ${className}`;

  // SVG URL - update to use /static prefix
  const iconUrl = `/static/img/icons/${weight}/${name}.svg?_fresh_cache=1`;

  // Render based on type
  if (type === "iconLink") {
    const { href, target, rel } = props;
    return (
      <a href={href} target={target} rel={rel}>
        <img
          src={iconUrl}
          className={combinedClasses}
          role={role}
          aria-label={ariaLabel || name}
          onClick={onClick}
          {...rest}
        />
      </a>
    );
  }

  if (type === "iconButton") {
    return (
      <button type="button" onClick={onClick}>
        <img
          src={iconUrl}
          className={combinedClasses}
          role={role}
          aria-label={ariaLabel || name}
          {...rest}
        />
      </button>
    );
  }

  return (
    <img
      src={iconUrl}
      className={combinedClasses}
      role={role}
      aria-label={ariaLabel || name}
      onClick={onClick}
      {...rest}
    />
  );
}

