// Template icon - duplicate the code and insert it above to add a new icon
const templateIcon = {
  light: "",
  normal: "",
  bold: "",
} as const;

/* ===== BASE ICON COMPONENT ===== */
export function Icon(props: IconVariants) {
  const {
    type,
    name,
    weight,
    size,
    color,
    className = "",
    role,
    ariaLabel,
    onClick,
    ...rest
  } = props;

  const combinedClasses = `${iconStyles.base} ${iconStyles.size[size]} ${
    iconStyles[type][color]
  } ${className}`;

  const commonProps = {
    className: combinedClasses,
    role,
    "aria-label": ariaLabel || name,
    onClick,
    ...rest,
  };

  /* ===== ICON PATH HANDLER ===== */
  const getIconPath = () => {
    switch (name) {
      case "twitter":
        return twitterIcon[weight as "light" | "normal" | "bold"];
      case "telegram":
        return telegramIcon[weight as "light" | "normal" | "bold"];
      case "github":
        return githubIcon[weight as "light" | "normal" | "bold"];
      case "discord":
        return discordIcon[weight as "light" | "normal" | "bold"];
      case "instagram":
        return instagramIcon[weight as "light" | "normal" | "bold"];
      case "website":
        return globeIcon[weight as "light" | "normal" | "bold"];
      case "tools":
        return gearIcon[weight as "light" | "normal" | "bold"];
      case "close":
        return xIcon[weight as "light" | "normal" | "bold"];
      case "donate":
        return handcoinsIcon[weight as "light" | "normal" | "bold"];
      case "caretUp":
        return caretUpIcon[weight as "light" | "normal" | "bold"];
      case "caretDown":
        return caretDownIcon[weight as "light" | "normal" | "bold"];
      case "caretLeft":
        return caretLeftIcon[weight as "light" | "normal" | "bold"];
      case "caretRight":
        return caretRightIcon[weight as "light" | "normal" | "bold"];
      case "caretDoubleLeft":
        return caretDoubleLeftIcon[weight as "light" | "normal" | "bold"];
      case "caretDoubleRight":
        return caretDoubleRightIcon[weight as "light" | "normal" | "bold"];
      default:
        return "";
      // Template icon - duplicate and insert above to add a new icon weight path
      case "template":
        return templateIcon[weight as "light" | "normal" | "bold"];
    }
  };

  /* ===== SHARED ICONS ===== */
  /* ===== SVG ELEMENT ===== */
  const svgElement = (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 32 32"
      {...commonProps}
    >
      <path d={getIconPath()} />
    </svg>
  );

  /* ===== RENDER BASED ON TYPE ===== */
  if (type === "iconLink") {
    const { href, target, rel } = props;
    return (
      <a href={href} target={target} rel={rel}>
        {svgElement}
      </a>
    );
  }

  if (type === "iconButton") {
    return (
      <button type="button" onClick={onClick}>
        {svgElement}
      </button>
    );
  }

  return svgElement;
}

/* ===== SPECIALIZED ICON COMPONENTS ===== */
/* ===== LAYOUT ===== */
/* ===== BADGE ICON ===== */
export function BadgeIcon({ text, className = "" }: BadgeVariants) {
  return (
    <span
      className={`
        flex items-center justify-center
        absolute top-0 left-0 z-10
        transform -translate-x-1/2 -translate-y-1/2 
        size-6 rounded-full
        font-bold text-xs text-stamp-grey group-hover:text-black tracking-wider
        bg-stamp-purple group-hover:bg-stamp-purple-bright
        transition-all duration-300
        ${text === "0" ? "opacity-0" : "opacity-100"}
        ${className}
      `}
    >
      {text}
    </span>
  );
}

/* ===== ICON COMPONENT DOCUMENTATION ===== */
/**
 * Icon Component
 *
 * How to add a new icon type:
 * 1. Add the icon path as a const using  the templateIcon code:
 *    const nameIcon = {
 *      light: "path_data_here",
 *      normal: "path_data_here",
 *      bold: "path_data_here"
 *    } as const;
 *
 * - nameIcon is the name of the icon type (globe instead of website)
 *
 * SVG Path Requirements:
 * - Provide all three weights: light, normal, bold
 * - SVG viewBox should be 32x32
 * - Export path data only, not full SVG element
 * - Use as const assertion for type safety
 * - Icons used on the site are from Figma Phosphor Icon Set
 * - https://www.figma.com/community/file/903830135544202908/phosphor-icons
 *
 * 2. Add the icon name with weights to getIconPath() function using the template
 * - Case "template, defines the icon name and default aria label, and the icon type to be assigned the name (case "website":
        return globeIcon[weight as "light" | "normal" | "bold"];)
  * - A custom aria-label can be added when inserting the icon, see the examples below

 *
 * @example
 * // Basic icon usage
 * <Icon
 *   type="icon"
 *   name="twitter"
 *   weight="light"
 *   size="sm"
 *   color="grey"
 * />
 *
 * // Icon as a link
 * <Icon
 *   type="iconLink"
 *   name="twitter"
 *   weight="light"
 *   size="md"
 *   color="purple"
 *   href="https://twitter.com"
 *   target="_blank"
 *   ariaLabel="Connect with us on Twitter"
 * />
 *
 * // Icon as a button with custom color
 * <Icon
 *   type="iconButton"
 *   name="close"
 *   weight="bold"
 *   size="lg"
 *   color="custom"
 *   className="fill-red-500 hover:fill-green-500"
 *   onClick={() => handleClose()}
 *   ariaLabel="Close Dialog"
 * />
 *
 */
